"use strict";var synth=new Tone.Synth;synth.oscillator.type="sine",synth.toMaster();var piano=document.getElementById("piano");piano.addEventListener("mousedown",function(t){synth.triggerAttack(t.target.dataset.note)}),piano.addEventListener("mouseup",function(t){synth.triggerRelease()}),document.addEventListener("keydown",function(t){switch(t.key){case"d":return synth.triggerAttack("C4");case"r":return synth.triggerAttack("C#4");case"f":return synth.triggerAttack("D4");case"t":return synth.triggerAttack("D#4");case"g":return synth.triggerAttack("E4");case"h":return synth.triggerAttack("F4");case"u":return synth.triggerAttack("F#4");case"j":return synth.triggerAttack("G4");case"i":return synth.triggerAttack("G#4");case"k":return synth.triggerAttack("A4");case"o":return synth.triggerAttack("A#4");case"l":return synth.triggerAttack("B4");default:return}}),document.addEventListener("keyup",function(t){switch(t.key){case"d":case"r":case"f":case"t":case"g":case"h":case"u":case"j":case"i":case"k":case"o":case"l":synth.triggerRelease()}});